using UnityEngine;using System.Collections;public class MainCamera : MonoBehaviour {	public float speed = 10f;	public bool isPlacing = false;	public Transform[] placements = new Transform[10];	int whereIAmAt = 0;	public Vector3 startPos, endPos;	public bool freeMove = false;	Vector3 lastPos;	public float factor;	// Use this for initialization	void Start() {	}	// Update is called once per frame	void Update() {		if (Input.GetKeyDown(KeyCode.DownArrow))			transform.position = new Vector3(transform.position.x, transform.position.y - 5.7f, transform.position.z);	    if (Input.GetKeyDown(KeyCode.UpArrow))			transform.position = new Vector3(transform.position.x, transform.position.y + 5.7f, transform.position.z);	    if (Input.GetKeyDown(KeyCode.LeftArrow) && !freeMove)			if (whereIAmAt > 0) {				whereIAmAt--;				transform.position = new Vector3(placements[whereIAmAt].position.x, transform.position.y,					placements[whereIAmAt].position.z);			}		if (Input.GetKeyDown(KeyCode.RightArrow) && !freeMove)			if (whereIAmAt < placements.Length - 1) {				whereIAmAt++;				transform.position = new Vector3(placements[whereIAmAt].position.x, transform.position.y,					placements[whereIAmAt].position.z);			}		if (Input.touchCount > 0 && !isPlacing && !freeMove) {			Touch touch = Input.GetTouch(0);			switch (touch.phase)			{				case TouchPhase.Began:					startPos = touch.position;					break;				case TouchPhase.Ended:					endPos = touch.position;					StaticMove();					break;			}		}		if (Input.touchCount > 0 && !isPlacing && freeMove) {			Touch touch = Input.GetTouch(0);			switch (touch.phase)			{				case TouchPhase.Began:					startPos = touch.position;					lastPos = touch.position;					break;				case TouchPhase.Moved:					FreeMove(new Vector3((touch.position.x - lastPos.x) * factor,						(touch.position.y - lastPos.y) * factor, 0f));					lastPos = touch.position;					break;				case TouchPhase.Ended:					endPos = touch.position;					//StaticMove();					break;			}		}	}	void StaticMove() {		if (startPos.x > endPos.x)		{			if (whereIAmAt < placements.Length - 1)			{				whereIAmAt++;				transform.position = placements[whereIAmAt].position;			}		}		else if (startPos.x < endPos.x)		{			if (whereIAmAt > 0)			{				whereIAmAt--;				transform.position = placements[whereIAmAt].position;			}		}	}	public void SetisPlacing(bool inc)	{		isPlacing = inc;	}	void FreeMove(Vector3 vec)	{		transform.position -= vec;	}}